import React, { useEffect, useState } from 'react';
import Table from './Table';
import DonutChart from './DonutChart';
import History from './History';

interface Position {
  asset: string;
  quantity: number;
  value: number;
}

const Dashboard: React.FC = () => {
  const [positions, setPositions] = useState<Position[]>([]);
  const [assets, setAssets] = useState<[]>([]);
  const [selectedPosition, setSelectedPosition] = useState<Position | null>(null);

  // Function to fetch the positions (this would normally come from your API)
  const getAssets = async () => {
    const response = await fetch('/api/assets', {
      method: "GET",
      headers: { "Accept": "application/json" }
  });
  if (response.ok === true) {
    const data = await response.json(); 
    setAssets(data);
  }
    // Simulating an API call to fetch the positions
    const mockPositions = [
      { asset: 'BTC', quantity: 0.5, value: 15000 },
      { asset: 'AAPL', quantity: 10, value: 2000 },
    ];
  };

  useEffect(() => {
    getAssets(); // Fetch positions when the component mounts
  }, []);

  // Function to handle row click (receives the clicked position)
  const handleRowClick = (position: Position) => {
    setSelectedPosition(position);
    console.log('Selected position:', position);
    // You can do further processing with the selected position here (e.g., show details)
  };

  return (
    <div className="dashboard-container">
      <h1>Portfolio Dashboard</h1>
      <div className="chart-container">
        <DonutChart data={assets} setSelectedAsset={()=>{}} selectedAsset={''} />
      </div>
      <div className="table-container">
      <Table positions={positions} onRowClick={handleRowClick} />
      </div>
      <div className="chart-container">
        <History data={assets} timeRange={''} setTimeRange={()=>{}} />
      </div>
      {selectedPosition && (
        <div className="selected-position">
          <h2>Selected Position Details</h2>
          <p>Asset: {selectedPosition.asset}</p>
          <p>Quantity: {selectedPosition.quantity}</p>
          <p>Value: {selectedPosition.value}</p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
